namespace lispa\amos\risultati\assets;

use yii\web\AssetBundle;

/**
 * Class AmosRisultatiAsset
 * @package lispa\amos\risultati\assets
 */
class AmosRisultatiAsset extends AssetBundle
{
    public $sourcePath = '@vendor/lispa/amos-risultati/src/assets/web';

    public $js = [
    ];

    public $css = [
        'css/style.css',
    ];

    public $depends = [
        'yii\web\JqueryAsset',
        'yii\jui\JuiAsset'
    ];

    public function init()
    {
        $moduleL = \Yii::$app->getModule('layout');
        if(!empty($moduleL))
        { $this->depends [] = 'lispa\amos\layout\assets\BaseAsset'; }
        else
        { $this->depends [] = 'lispa\amos\core\views\assets\AmosCoreAsset'; }
        parent::init(); // TODO: Change the autogenerated stub
    }
}