namespace open20\amos\faq\assets;

use yii\web\AssetBundle;

class AmosFaqAsset extends AssetBundle
{
    /**
     * @var string
     */
    public $sourcePath = '@vendor/open20/amos-faq/src/assets/web/';

    /**
     * @var array
     */
    public $css = [
        'less/faq.less',
    ];

    /**
     * @var array
     */
    public $js = [
    ];

    /**
     * @var array
     */
    public $depends = [
    ];

    public function init()
    {
        $moduleL = \Yii::$app->getModule('layout');
        if(!empty($moduleL))
        { $this->depends [] = 'open20\amos\layout\assets\BaseAsset'; }
        else
        { $this->depends [] = 'open20\amos\core\views\assets\AmosCoreAsset'; }
        parent::init(); // TODO: Change the autogenerated stub
    }

}